'use strict';

const fs = require('node:fs');
const path = require('node:path');
const sloc = require('sloc');
const escomplex = require('typhonjs-escomplex');

function listJsFiles(dir) {
  const out = [];
  for (const entry of fs.readdirSync(dir, { withFileTypes: true })) {
    const p = path.join(dir, entry.name);
    if (entry.isDirectory()) out.push(...listJsFiles(p));
    if (entry.isFile() && p.endsWith('.js')) out.push(p);
  }
  return out;
}

function readFile(p) {
  return fs.readFileSync(p, 'utf8');
}

function ensureReportsDir() {
  const dir = path.join(process.cwd(), 'reports');
  fs.mkdirSync(dir, { recursive: true });
}

function computeSloc(files) {
  const byFile = {};
  const total = { source: 0, comment: 0, empty: 0, total: 0 };

  for (const f of files) {
    const s = readFile(f);
    const r = sloc(s, 'js');
    byFile[path.relative(process.cwd(), f)] = r;
    total.source += r.source;
    total.comment += r.comment;
    total.empty += r.empty;
    total.total += r.total;
  }

  return { total, byFile };
}

function computeComplexity(moduleFiles) {
  const out = {};
  for (const f of moduleFiles) {
    const s = readFile(f);
    // typhonjs-escomplex supports analyzing module source.
    const report = escomplex.analyzeModule(s, { filePath: f });
    out[path.relative(process.cwd(), f)] = {
      aggregate: report.aggregate,
      methods: report.methods?.map(m => ({ name: m.name, cyclomatic: m.cyclomatic })) ?? []
    };
  }
  return out;
}

function writeJson(file, data) {
  fs.writeFileSync(path.join(process.cwd(), file), JSON.stringify(data, null, 2));
}

function writeMarkdown(file, content) {
  fs.writeFileSync(path.join(process.cwd(), file), content);
}

function main() {
  ensureReportsDir();

  const allFiles = [
    ...listJsFiles(path.join(process.cwd(), 'src')),
    ...listJsFiles(path.join(process.cwd(), 'test')),
    ...listJsFiles(path.join(process.cwd(), 'scripts'))
  ];

  const slocReport = computeSloc(allFiles);
  writeJson('reports/sloc.json', slocReport);

  const complexityReport = computeComplexity(listJsFiles(path.join(process.cwd(), 'src')));
  writeJson('reports/complexity.json', complexityReport);

  const md = [
    '# Metrics Summary',
    '',
    `Generated: ${new Date().toISOString()}`,
    '',
    '## SLOC (JavaScript)',
    '',
    `- Total lines: ${slocReport.total.total}`,
    `- Source lines: ${slocReport.total.source}`,
    `- Comment lines: ${slocReport.total.comment}`,
    `- Empty lines: ${slocReport.total.empty}`,
    '',
    '## Complexity (typhonjs-escomplex)',
    ''
  ];

  for (const [file, rep] of Object.entries(complexityReport)) {
    md.push(`- ${file}: cyclomatic=${rep.aggregate?.cyclomatic ?? 'n/a'}, maintainability=${rep.aggregate?.maintainability ?? 'n/a'}`);
  }

  md.push('', 'Artifacts:', '- reports/sloc.json', '- reports/complexity.json');
  writeMarkdown('reports/metrics-summary.md', md.join('\n'));

  console.log('Metrics written to reports/');
}

main();

'use strict';

const fs = require('node:fs');
const path = require('node:path');

function main() {
  const eslintPath = path.join(process.cwd(), 'reports', 'eslint.json');
  if (!fs.existsSync(eslintPath)) {
    console.log('No reports/eslint.json found. Run: npm run lint');
    process.exit(0);
  }

  const data = JSON.parse(fs.readFileSync(eslintPath, 'utf8'));

  const counts = {};
  const top = [];

  for (const file of data) {
    for (const msg of file.messages) {
      const rule = msg.ruleId ?? 'unknown';
      counts[rule] = (counts[rule] ?? 0) + 1;
      top.push({
        file: file.filePath,
        line: msg.line,
        col: msg.column,
        rule,
        message: msg.message
      });
    }
  }

  const sortedRules = Object.entries(counts).sort((a, b) => b[1] - a[1]);
  const md = [];
  md.push('# Bad Smells Summary');
  md.push('');
  md.push(`Generated: ${new Date().toISOString()}`);
  md.push('');
  md.push('This project uses ESLint + plugins (sonarjs, unicorn, import) as an automated smell detector.');
  md.push('');
  md.push('## Rule counts');
  md.push('');
  if (sortedRules.length === 0) {
    md.push('- No lint findings ');
  } else {
    for (const [rule, count] of sortedRules) {
      md.push(`- ${rule}: ${count}`);
    }
  }

  md.push('');
  md.push('## First findings (up to 10)');
  md.push('');
  for (const item of top.slice(0, 10)) {
    const rel = path.relative(process.cwd(), item.file);
    md.push(`- ${rel}:${item.line}:${item.col} ${item.rule}  ${item.message}`);
  }

  md.push('');
  md.push('Artifacts:');
  md.push('- reports/eslint.json');

  fs.writeFileSync(path.join(process.cwd(), 'reports', 'smells-summary.md'), md.join('\n'));
  console.log('Bad smells summary written to reports/smells-summary.md');
}

main();

#!/usr/bin/env node
'use strict';

const fs = require('node:fs');
const { compare } = require('./calc');

/**
 * Input format (JSON):
 * {
 *   "electricityPricePerKWh": 0.35,
 *   "years": 10,
 *   "applianceA": { "name": "...", "purchasePrice": 499, "powerW": 900, "hoursPerUse": 1.5, "usesPerWeek": 5 },
 *   "applianceB": { "name": "...", "purchasePrice": 399, "powerW": 1100, "hoursPerUse": 1.5, "usesPerWeek": 5 }
 * }
 */

function readStdin() {
  return fs.readFileSync(0, 'utf8').trim();
}

function formatMoneyEUR(amount) {
  return new Intl.NumberFormat('de-DE', { style: 'currency', currency: 'EUR' }).format(amount);
}

function formatNumber(n, digits = 2) {
  return new Intl.NumberFormat('de-DE', { minimumFractionDigits: digits, maximumFractionDigits: digits }).format(n);
}

function main() {
  const raw = readStdin();
  const input = JSON.parse(raw);

  const scenario = {
    electricityPricePerKWh: input.electricityPricePerKWh,
    years: input.years
  };

  const a = input.applianceA;
  const b = input.applianceB;

  const result = compare(a, b, scenario);

  const nameA = a.name ?? 'Appliance A';
  const nameB = b.name ?? 'Appliance B';

  const pretty = {
    scenario,
    applianceA: { name: nameA, ...result.totals.A },
    applianceB: { name: nameB, ...result.totals.B },
    cheaper: result.cheaper === 'A' ? nameA : result.cheaper === 'B' ? nameB : 'equal',
    difference: result.difference
  };

  // Human-readable output
  process.stdout.write(`Scenario: electricity ${formatMoneyEUR(scenario.electricityPricePerKWh)} per kWh, duration ${scenario.years} years\n\n`);

  for (const [label, item] of [
    ['A', pretty.applianceA],
    ['B', pretty.applianceB]
  ]) {
    process.stdout.write(`${label}) ${item.name}\n`);
    process.stdout.write(`   Purchase:       ${formatMoneyEUR(item.purchaseCost)}\n`);
    process.stdout.write(`   kWh per use:    ${formatNumber(item.kWhPerUse)} kWh\n`);
    process.stdout.write(`   kWh per year:   ${formatNumber(item.yearlyKWh)} kWh\n`);
    process.stdout.write(`   Energy / year:  ${formatMoneyEUR(item.yearlyEnergyCost)}\n`);
    process.stdout.write(`   Energy total:   ${formatMoneyEUR(item.totalEnergyCost)}\n`);
    process.stdout.write(`   TOTAL:          ${formatMoneyEUR(item.totalCost)}\n\n`);
  }

  if (result.cheaper === 'equal') {
    process.stdout.write('Result: Both appliances have equal total cost.\n');
  } else {
    const cheaperName = result.cheaper === 'A' ? nameA : nameB;
    process.stdout.write(`Result: Cheaper is ${cheaperName} by ${formatMoneyEUR(result.difference)} over ${scenario.years} years.\n`);
  }

  // Machine-readable JSON (last line) for automation
  process.stdout.write(`\nJSON_RESULT=${JSON.stringify(pretty)}\n`);
}

main();
